import API_BASE_URL from '../config';
import React, { useState } from 'react';
import axios from 'axios';

function CompteForm() {
  // formulaire
  const [compte, setCompte] = useState({
    solde: '',
    dateCreation: '',
    type: 'COURANT',
  });

  // Gerer la saisie utilisateur
  const handleChange = (e) => {
    setCompte({ ...compte, [e.target.name]: e.target.value });
  };

  // Soumettre le formulaire
  const handleSubmit = (e) => {
    e.preventDefault();

    // Preparer les donnees correctement 
    const dataToSend = {
      solde: parseFloat(compte.solde), // convertir en nombre
      dateCreation: compte.dateCreation, // format ISO (OK pour Spring)
      type: compte.type.toUpperCase(), // en majuscules pour matcher l'enum
    };

    axios
      .post(`${API_BASE_URL}/comptes`, dataToSend, {
        headers: { 'Content-Type': 'application/json' },
      })
      .then(() => {
        alert('✅ Compte ajouté avec succès !');
        window.location.reload(); // recharge la liste
      })
      .catch((error) => {
        console.error(' Erreur lors de l ajout du compte :', error);
        alert('Erreur : impossible d ajouter le compte');
      });
  };

  return (
    <div className="container mt-4">
      <h2>Ajouter un Compte</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Solde</label>
          <input
            type="number"
            name="solde"
            className="form-control"
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label>Date de Création</label>
          <input
            type="date"
            name="dateCreation"
            className="form-control"
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label>Type</label>
          <select
            name="type"
            className="form-select"
            onChange={handleChange}
            required
          >
            <option value="COURANT">Courant</option>
            <option value="EPARGNE">Épargne</option>
          </select>
        </div>

        <button type="submit" className="btn btn-primary">
          Ajouter
        </button>
      </form>
    </div>
  );
}

export default CompteForm;
